INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('E43', 'E40-E46', 'Тяжелая белково-энергетическая недостаточность неуточненная', 'Тяжелая потеря массы тела у детей или взрослых или отсутствие прибавки массы тела у ребенка, которые приводят к тому, что выявляемая масса тела оказывается как минимум на 3 стандартных отклонения ниже среднего показателя для эталонной группы (или подобное снижение массы тела, отраженное другими статистическими методами). Если в распоряжении имеются данные лишь однократного измерения массы тела, то о тяжелом истощении с большой степенью вероятности можно говорить, когда выявленная масса тела на 3 или более стандартных отклонений ниже среднего показателя для эталонной группы населения. Голодный отек');
