INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('F71', 'F70-F79', 'Умственная отсталость умеренная', 'Ориентировочный IQ колеблется от 35 до 49 (в зрелом возрасте умственное развитие соответствует развитию в возрасте 6-9 лет). Вероятно заметное отставание в развитии с детства, но большинство может обучаться и достичь определенной степени независимости в самообслуживании, приобрести адекватные коммуникационные и учебные навыки. Взрослые будут нуждаться в разных видах поддержки в быту и на работе. Включена: умственная субнормальность средней тяжести');
