INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('F72', 'F70-F79', 'Умственная отсталость тяжелая', 'Ориентировочный IQ колеблется от 20 до 34 (в зрелом возрасте умственное развитие соответствует развитию в возрасте 3-6 лет). Вероятна необходимость постоянной поддержки. Включена: резко выраженная умственная субнормальность');
