INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('F73', 'F70-F79', 'Умственная отсталость глубокая', 'Ориентировочный IQ ниже 20 (в зрелом возрасте умственное развитие ниже развития в трехлетнем возрасте). Результатом является тяжелое ограничение самообслуживания, коммуникабельности и подвижности, а также повышенная сексуальность. Включена: глубокая умственная субнормальность');
