INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('G00-G99', NULL, 'БОЛЕЗНИ НЕРВНОЙ СИСТЕМЫ', 'Исключены: отдельные состояния, возникающие в перинатальном периоде (P00-P96) некоторые инфекционные и паразитарные болезни (A00-B99) осложнения беременности, родов и послеродового периода (O00-099) врожденные аномалии, деформации и хромосомные нарушения (Q00-Q99) болезни эндокринной системы, расстройства питания и нарушения обмена веществ (E00-E90) травмы, отравления и некоторые другие последствия воздействия внешних причин (S00-T98) новообразования (C00-D48) симптомы, признаки и отклонения от нормы, выявленные при клинических и лабораторных исследованиях, не классифицированные в других рубриках (R00-R99)
