INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('P05.2', 'P05', 'Недостаточность питания плода без упоминания о "маловесном" или маленьком для гестационного возраста', 'Новорожденный, у которого нет снижения массы тела, но отмечаются признаки недостаточности питания, такие, как сухость, шелушение кожи и неполноценность подкожной клетчатки. Исключены: недостаточность питания плода с упоминанием о: . "маловесности" для гестационного возраста (P05.0) . малом размере для гестационного возраста (P05.1)');
