INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z60.4', 'Z60', 'Социальная изоляция и остракизм', 'Социальная изоляция и остракизм на основе личностных характеристик, таких, как необычная внешность, болезнь или поведение. Исключена: жертва враждебной дискриминации по расовому или религиозному признаку (Z60.5)');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z60.5', 'Z60', 'Жертва ощущаемой дискриминации или гонения', 'Преследование или дискриминация (предполагаемые или реальные), основанные на принадлежности к некоторым группам (по цвету кожи, религии, этническому происхождению и т.д.), а не личностных характеристиках. Исключены: социальная изоляция и остракизм (Z60.4)');
