INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61', 'Z55-Z65', 'Проблемы, связанные с неблагоприятными жизненными событиями в детстве', 'Исключен: синдром жестокого обращения (T74.-)');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.0', 'Z61', 'Потеря в детстве любимых родственников', 'Потеря эмоционально близких родственников, таких, как родители, брат или сестра, очень близкий друг или любимый человек, вследствие смерти, длительного отсутствия или отстранения.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.1', 'Z61', 'Отлучение ребенка от дома', 'Помещение в приют, больницу или другое учреждение, вызывающее психологический стресс, или призыв в армию из дома на длительный период.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.2', 'Z61', 'Изменение семейных взаимоотношений родственников в детстве', 'Появление другого лица в семье в результате неблагоприятных для ребенка изменений во взаимоотношениях родственников (это может быть новый брак родителей или рождение другого ребенка).');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.3', 'Z61', 'События, приводящие к снижению самооценки в детстве', 'События, завершающиеся самооценкой ребенка (например, неудача каком-либо деле с высокой личной ответственностью, обнаружение или раскрытие постыдных или бесчестных эпизодов личной или семейной жизни и другие факторы, дающие повод к самоуничижению).');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.4', 'Z61', 'Проблемы, связанные с возможным сексуальным изнасилованием ребенка лицом, принадлежащим к группе первичной поддержки', 'Проблемы, связанные с какой-либо формой физического контакта между взрослым членом семьи и ребенком или другими способами, вызывающими сексуальное возбуждение и независимо от желания ребенка приводящее к половому контакту (например, прикосновения или манипуляции с половыми органами, намеренное обнажение половых органов или молочных желез).');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.5', 'Z61', 'Проблемы, связанные с возможным изнасилованием ребенка посторонним лицом', 'Проблемы, связанные с различными манипуляциями с половыми органами и молочными железами, с раздеванием, ласками и другими действиями с ребенком, склоняющими или имеющими целью склонять его к половому акту, лицом, значительно более старшим по возрасту, не являющимся членом семьи и использующим свой статус или положение, либо действующим против желания ребенка.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.6', 'Z61', 'Проблемы, связанные с возможным физическим насилием по отношению к ребенку', 'Проблемы, связанные с инцидентами, при которых ребенку в прошлом были нанесены травмы кем-либо из взрослых, проживающих в доме, требующие медицинского ухода (переломы, явные кровоподтеки), либо при которых ребенок подвергся жестоким формам насилия (избиение тяжелыми или острыми предметами, ожоги или связывание).');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.7', 'Z61', 'Личные потрясения, перенесенные в детстве', 'Переживания, которые могут оказать влияние на будущее ребенка, вызванные например, похищением, природными катаклизмами с угрозой для жизни, увечьем, угрожающим безопасности или собственному восприятию, или травмой, нанесенной дорогому для ребенка человеку в его присутствии.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.8', 'Z61', 'Другие неблагоприятные жизненные события в детстве', NULL);
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z61.9', 'Z61', 'Неблагоприятное жизненное событие в детстве неуточненное', NULL);
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62', 'Z55-Z65', 'Другие проблемы, связанные с воспитанием ребенка', 'Исключен: синдром жестокого обращения (T74.-)');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.0', 'Z62', 'Неадекватность заботы и контроля со стороны родителей', 'Неинформированность родителей о том, что делает ребенок и где он находится, плохой контроль за ним, отсутствие постоянной заботы о нем и попыток предупредить рискованные ситуации, в которых он может оказаться.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.1', 'Z62', 'Излишняя опека со стороны родителей', 'Система воспитания, результатом которой является инфантильность и отсутствие у ребенка самостоятельности и независимости.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.2', 'Z62', 'Воспитание в закрытом учреждении', 'Групповое воспитание, при котором ответственность родителей в значительной степени переложена на персонал различных форм учреждений (таких, как дома ребенка, приюты для сирот, школы-интернаты) или терапевтическая помощь в течение длительного времени в больнице, учреждении для выздоравливающих или санатории, когда ребенок находится по крайней мере без одного родителя.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.3', 'Z62', 'Враждебное отношение и несправедливые претензии к ребенку', 'Негативное отношение родителей к ребенку как к личности, резкость и постоянная раздражительность по отношению к некоторым моментам в поведении ребенка (например, постоянные оскорбления за любые поступки в доме или напрасные обвинения ребенка).');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.4', 'Z62', 'Эмоциональная заброшенность детей', 'Тон разговора родителей с ребенком пренебрежительный или безразличный. Отсутствие интереса к ребенку, чуткого отношения к его трудностям, похвалы и поддержки, раздражительная реакция на нарушение в поведении ребенка и отсутствие ласкового и теплого отношения к ребенку.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.5', 'Z62', 'Другие проблемы, связанные с недостатками в воспитании', 'Отсутствие обучающего и игрового опыта у ребенка');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.6', 'Z62', 'Неприемлемое давление со стороны родителей и другие отрицательные факторы воспитания', 'Родители заставляют ребенка делать то, что выходит за рамки принятых норм, не соответствует половой принадлежности (например, одевание мальчика в девичье платье), возрасту (например, требование от ребенка еще недоступной ему ответственности за свои поступки), его желанию или возможностям.');
INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z62.8', 'Z62', 'Другие уточненные проблемы, связанные с воспитанием ребенка', NULL);
