INSERT INTO ref_mkb10 (id, parent_id, name, comments) VALUES ('Z63.0', 'Z63', 'Проблемы, связанные с взаимоотношениями супругов или партнеров', 'Разногласия между супругами (партнерами), ведущие к длительной или выраженной утрате контроля взаимоотношений, враждебности, нежеланию понять друг друга или постоянной атмосфере грубого межличностного насилия (избиения, драки).');
