INSERT INTO todo_task (id, title, info, project_id, status_id, author, assigned_to, created_at, has_deadline) VALUES ('2', 'Начать работу с opentodo', 'Теперь вы можете добавить пользователей через админ-панель django, создать новый проект (пункт меню Проекты) и начать работу.', '1', '1', '1', '1', NOW(),'0');
INSERT INTO todo_task (id, title, info, project_id, status_id, author, assigned_to, created_at, has_deadline) VALUES ('1', 'Установить opentodo', 'Если вы читаете это, то установка прошла успешно!\nНажмите кнопку "Одобрить", чтобы перевести задачу в статус "Завершена, Одобрена".\nДля краткости этот статус будет отображаться как "Завершена", но цвет фона изменится на серый.', '1', '3', '1', '1', DATE_ADD(NOW(), INTERVAL -1 MINUTE),'0');